import os
import torch
import numpy as np
import copy

def Set_Parameters(info):
    States = []
    actors = [actor for actor in info.keys()]                   # actors : ['car1', 'car2', 'car3', 'car4']
    count = 0
    for car_id in actors:
        tmp = []
        dump_actors = copy.deepcopy(actors)
        del dump_actors[count]
        tmp.append(info[car_id]['x']-info[car_id]['end_pos'][0])
        tmp.append(info[car_id]['y']-info[car_id]['end_pos'][1])

        for i in range(len(dump_actors)):
            tmp.append(info[car_id]['x']-info[dump_actors[i]]['x'])                     ### x 좌표

        for j in range(len(dump_actors)):
            tmp.append(info[car_id]['y']-info[dump_actors[j]]['y'])                     ### y 좌표

        for k in range(len(dump_actors)):
            tmp.append(info[car_id]['v_x']-info[dump_actors[k]]['v_x'])                 ### x 축 속도

        for m in range(len(dump_actors)):
            tmp.append(info[car_id]['v_y']-info[dump_actors[m]]['v_y'])                 ### y축 속도
        States.append(tmp)
        count += 1
    
    return np.array(States)